import React, { Component } from "react";

class Timer extends Component {
  state = {
    time: "0",
    hour: "0",
    min: "0",
    sec: "0",
  };

  timeConvert = () => {
    var num = this.state.time;
    this.setState({ hour: Math.floor(num / 3600) });
    this.setState({ minute: Math.floor(num - 3600 * num) / 60 });
    this.setState({ second: num % 60 });
  };
  render() {
    return (
      <div className="timer">
        <h1>Type your time</h1>
        <input
          type="text"
          id="time"
          placeholder="Time"
          onChange={(e) => this.setState({ time: e.target.value })}
        />
        <button type="button" onClick={this.timeConvert}>
          Convert
        </button>
        <br />
        <span>
          <input
            type="text"
            id="hour"
            placeholder="Hour"
            value={this.state.hour}
          />
          <input
            type="text"
            id="min"
            placeholder="Minute"
            value={this.state.min}
          />
          <input
            type="text"
            id="sec"
            placeholder="Seconds"
            value={this.state.sec}
          />
        </span>
      </div>
    );
  }
}
export default Timer;
